import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  Button,
  TextField,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Box,
  CircularProgress,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  IconButton,
} from '@mui/material';
import { ethers } from 'ethers';
import DeleteIcon from '@mui/icons-material/Delete';
import TokenLockup from '../contracts/TokenLockup.json';

function Vault() {
  const [vaults, setVaults] = useState([]);
  const [openDialog, setOpenDialog] = useState(false);
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    token: '',
    amount: '',
    lockTime: '',
    memoryURI: '',
    subscriptionFee: '',
    yieldRate: '',
  });

  useEffect(() => {
    fetchVaults();
  }, []);

  const fetchVaults = async () => {
    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(
        process.env.REACT_APP_TOKEN_LOCKUP_ADDRESS,
        TokenLockup.abi,
        signer
      );

      const userVaults = await contract.getUserVaults(await signer.getAddress());
      const vaultPromises = userVaults.map(id => contract.getVault(id));
      const vaultData = await Promise.all(vaultPromises);
      setVaults(vaultData);
    } catch (error) {
      console.error('Error fetching vaults:', error);
    }
  };

  const handleCreateVault = async () => {
    try {
      setLoading(true);
      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(
        process.env.REACT_APP_TOKEN_LOCKUP_ADDRESS,
        TokenLockup.abi,
        signer
      );

      const tx = await contract.createVault(
        formData.token,
        ethers.parseEther(formData.amount),
        ethers.parseEther(formData.lockTime),
        formData.memoryURI,
        ethers.parseEther(formData.subscriptionFee),
        formData.yieldRate
      );

      await tx.wait();
      setOpenDialog(false);
      fetchVaults();
    } catch (error) {
      console.error('Error creating vault:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleUnlockVault = async (vaultId) => {
    try {
      setLoading(true);
      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(
        process.env.REACT_APP_TOKEN_LOCKUP_ADDRESS,
        TokenLockup.abi,
        signer
      );

      const tx = await contract.unlockVault(vaultId);
      await tx.wait();
      fetchVaults();
    } catch (error) {
      console.error('Error unlocking vault:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAddMemory = async (vaultId, note, imageURI) => {
    try {
      setLoading(true);
      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(
        process.env.REACT_APP_TOKEN_LOCKUP_ADDRESS,
        TokenLockup.abi,
        signer
      );

      const tx = await contract.addMemory(vaultId, note, imageURI);
      await tx.wait();
      fetchVaults();
    } catch (error) {
      console.error('Error adding memory:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="lg">
      <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 4 }}>
        <Typography variant="h4" component="h1">
          Time-Locked Vaults
        </Typography>
        <Button
          variant="contained"
          color="primary"
          onClick={() => setOpenDialog(true)}
        >
          Create Vault
        </Button>
      </Box>

      <Grid container spacing={3}>
        {vaults.map((vault) => (
          <Grid item xs={12} md={6} key={vault.vaultId.toString()}>
            <Card>
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  Vault #{vault.vaultId.toString()}
                </Typography>
                <Typography color="text.secondary">
                  Amount: {ethers.formatEther(vault.amount)} tokens
                </Typography>
                <Typography color="text.secondary">
                  Lock Time: {new Date(vault.unlockTime * 1000).toLocaleDateString()}
                </Typography>
                <Typography color="text.secondary">
                  Yield Rate: {vault.yieldRate.toString()}%
                </Typography>
                <Typography color="text.secondary">
                  Status: {vault.isActive ? 'Active' : 'Unlocked'}
                </Typography>
              </CardContent>
              <Box sx={{ p: 2 }}>
                {vault.isActive ? (
                  <Button
                    variant="contained"
                    color="primary"
                    fullWidth
                    onClick={() => handleUnlockVault(vault.vaultId)}
                    disabled={loading || Date.now() < vault.unlockTime * 1000}
                  >
                    Unlock Vault
                  </Button>
                ) : (
                  <Typography color="text.secondary" align="center">
                    Vault has been unlocked
                  </Typography>
                )}
              </Box>
            </Card>
          </Grid>
        ))}
      </Grid>

      <Dialog open={openDialog} onClose={() => setOpenDialog(false)}>
        <DialogTitle>Create Time-Locked Vault</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Token Address"
            type="text"
            fullWidth
            value={formData.token}
            onChange={(e) => setFormData({ ...formData, token: e.target.value })}
          />
          <TextField
            margin="dense"
            label="Amount"
            type="number"
            fullWidth
            value={formData.amount}
            onChange={(e) => setFormData({ ...formData, amount: e.target.value })}
          />
          <TextField
            margin="dense"
            label="Lock Time (days)"
            type="number"
            fullWidth
            value={formData.lockTime}
            onChange={(e) => setFormData({ ...formData, lockTime: e.target.value })}
          />
          <TextField
            margin="dense"
            label="Memory URI"
            type="text"
            fullWidth
            value={formData.memoryURI}
            onChange={(e) => setFormData({ ...formData, memoryURI: e.target.value })}
          />
          <TextField
            margin="dense"
            label="Subscription Fee (MON)"
            type="number"
            fullWidth
            value={formData.subscriptionFee}
            onChange={(e) => setFormData({ ...formData, subscriptionFee: e.target.value })}
          />
          <TextField
            margin="dense"
            label="Yield Rate (%)"
            type="number"
            fullWidth
            value={formData.yieldRate}
            onChange={(e) => setFormData({ ...formData, yieldRate: e.target.value })}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDialog(false)}>Cancel</Button>
          <Button
            onClick={handleCreateVault}
            variant="contained"
            disabled={loading}
          >
            {loading ? <CircularProgress size={24} /> : 'Create'}
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}

export default Vault;
